import React from "react";

const educationData = [
  {
    title: "10TH STANDARD",
    logo: "/images/narayana-logo.png", // Replace with actual logo path
    school: "Narayana e-techno School",
    year: "2016",
    details: ["CGPA: 8.3", "Course: SSC"],
  },
  {
    title: "INTERMEDIATE",
    logo: "/images/narayana-logo.png", // Replace with actual logo path
    school: "Narayana Jr College",
    year: "2016-2018",
    details: ["Percentage: 80%", "Course: MPC"],
  },
  {
    title: "BACHELOR'S DEGREE",
    logo: "/images/sr-university-logo.png", // Replace with actual logo path
    school: "SR University",
    year: "2018-2022",
    details: ["Course: B.Tech", "Major: Computer Science"],
  },
];

const Education = () => {
  return (
    <div className="bg-blue-50 py-12 flex flex-col items-center">
      <h1 className="text-3xl md:text-5xl font-bold uppercase tracking-wide text-gray-900 mb-8">
        Education
      </h1>
      <div className="flex flex-wrap justify-center gap-6 px-4">
        {educationData.map((edu, index) => (
          <div
            key={index}
            className="w-80 bg-white shadow-lg rounded-2xl p-6 text-center transform transition duration-300 hover:scale-105"
          >
            <h4 className="text-lg font-semibold text-gray-700 uppercase mb-2">
              {edu.title}
            </h4>
            <div className="flex justify-center">
              <img
                src={edu.logo}
                alt={`${edu.school} Logo`}
                className="w-24 h-24 object-contain mb-4"
              />
            </div>
            <h3 className="text-lg font-medium text-gray-900">{edu.school}</h3>
            <p className="text-sm text-gray-600">{edu.year}</p>
            <ul className="mt-2 text-sm text-gray-700">
              {edu.details.map((detail, i) => (
                <li key={i}>{detail}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Education;
