import React from "react";
import { motion } from "framer-motion"; // Animation Library
import "./Experience.scss";

export default function Experience() {
  const currentYear = new Date().getFullYear();
  const data = [
    {
      id: 1,
      name: "ZF Groups, Hyderabad",
      year: `2022 - ${currentYear}`,
      title: "SIL Engineer",
      role: "I work as an ADAS SIL Integrator, focusing on integrating, testing, and validating software systems designed for advanced driver-assistance systems (ADAS). My role involves creating a virtual testing environment where I simulate real-world driving scenarios to validate the functionality, performance, and safety of ADAS software.",
      tools: "SIL System Integration, C++, Python, QT, Docker, Linux",
      icon: "assets/ZF.svg.png",
      featured: true,
    },
  ];

  return (
    <div className="experience" id="Experience">
      <h1>Experience</h1>
      <div className="container">
        {data.map((d) => (
          <motion.div 
            key={d.id} 
            className={d.featured ? "card featured" : "card"}
            whileHover={{ scale: 1.05 }} 
            initial={{ opacity: 0, y: 50 }} 
            animate={{ opacity: 1, y: 0 }} 
            transition={{ duration: 0.6 }}
          >
            <div className="top">
              <motion.img 
                className="icon" 
                src={d.icon} 
                alt={`${d.name} logo`} 
                whileHover={{ scale: 1.1 }} 
              />
            </div>
            <div className="content">
              <h2 className="name">{d.name}</h2>
              <h3 className="title">{d.title}</h3>
              <p className="year">{d.year}</p>
              <p className="role">{d.role}</p>
              <p className="tools"><strong>Tools:</strong> {d.tools}</p>
            </div>
          </motion.div>
        ))}
      </div>
    </div>
  );
}
