import "./intro.scss";
import { init } from "ityped";
import { useEffect, useRef } from "react";
import { FaLinkedin } from "react-icons/fa"; // Import LinkedIn icon

export default function Intro() {
  const textRef = useRef();

  useEffect(() => {
    init(textRef.current, {
      showCursor: true,
      backDelay: 1500,
      backSpeed: 60,
      strings: ["Developer", "SIL-Integrator", "Learner", "Innovator"],
    });
  }, []);

  return (
    <div className="intro" id="intro">
      <div className="left">
        <div className="imgContainer">
          <img src="assets/vg.JPG" alt="Venugopal Sai Mekala" />
        </div>
      </div>
      <div className="right">
        <div className="wrapper">
          <h2>Hi There, I'm</h2>
          <h1>Venugopal Sai Mekala</h1>
          <h3>
            <span ref={textRef}></span>
          </h3>
          {/* LinkedIn Profile Link */}
          <a
            href="https://www.linkedin.com/in/mekala-venugopal-sai-b0b376195/"
            target="_blank"
            rel="noopener noreferrer"
            className="linkedin-link"
          >
            <FaLinkedin size={30} color="#0e76a8" />
          </a>
        </div>
        <a href="#Experience">
          <img src="assets/down.png" alt="Scroll Down" />
        </a>
      </div>
    </div>
  );
}
